package com.galaxe.metrics.ServiceImpl;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.mail.javamail.JavaMailSender;
import org.springframework.mail.javamail.MimeMessageHelper;
import org.springframework.stereotype.Service;

import com.galaxe.metrics.Service.EmailService;

import jakarta.mail.MessagingException;
import jakarta.mail.internet.MimeMessage;

@Service
public class EmailServiceImpl implements EmailService{

	@Autowired
	 private JavaMailSender javaMailSender;

	@Override
	public void sendBirthdayMail(String to) {
		try {
			MimeMessage mimeMessage=javaMailSender.createMimeMessage();
			MimeMessageHelper helper=new MimeMessageHelper(mimeMessage,true);
			
			helper.setTo(to);
			helper.setSubject("Birthday Greetings");
			
			String content="<html><body><img src='data:image/jpeg;base64,' alt=''/>"+
			"<p style='text-align:center; '><br></br>Wish you a very Happy Birthday./>";
			helper.setText(content,true);
			
			javaMailSender.send(mimeMessage);
		} catch (MessagingException e) {
			e.printStackTrace();
		}
		
	}
}
